import React,{useState} from 'react'
import Fab from '@mui/material/Fab';
import Zoom from '@mui/material/Zoom';
import AddIcon from '@mui/icons-material/Add';
import { Expand } from '@mui/icons-material';

const Create_Note = () => {
    const[isExpanded,setIsExpended]= useState(false);

    const[note,setNote]=useState({
        title:"",
        content:""
    });

    const expand = ()=>{
        setIsExpended(true);
    };
    const handleChang=(even)=>{
        const {name, value}=even.target;
        setNote((prevNote)=>{
            return{
                ...prevNote,
                
            }
        })
        console.log(note)
    }   
    
    return (
        <div>
             <form className="create-note">
             {isExpanded && 
                  <input name="title" placeholder="Title"  type="text" onChange={handleChang}/> }
                    <textarea name="content" placeholder="Take a note" onClick={expand} onChange={handleChang} />
                  
                    <Zoom in={isExpanded}>
                        <Fab onClick={()=>{}}>
                              <AddIcon />
                        </Fab>
                    </Zoom>
             </form>
        </div>
    )
}

export default Create_Note
